sys_script_fix.script:
var dictionary_id;
try {
	var gr_dict = new GlideRecord('ts_synonym_dictionary');
	gr_dict.initialize();
	gr_dict.name='Imported from HR Ontology';
	gr_dict.insert();
	dictionary_id=gr_dict.sys_id;
	gs.info("HR Ontology synonym dictionary found, id is " +dictionary_id);
	var r = new sn_ws.RESTMessageV2('x_463104_socrates.Enrichment', 'GetAllKeywords');
	var response = r.execute();
	var responseBody = response.getBody();
	var httpStatus = response.getStatusCode();
	var concepts = JSON.parse(responseBody);
	concepts.forEach(process);
	gs.info(concepts.length + ' concepts processed');
}
catch(ex) {
	var message = ex.message;
}

function process(concept){
	var gr = new GlideRecord('ts_synonym_set');
	gr.initialize();
	gr.dictionary=dictionary_id;
	gr.keyword = concept.preferredName;
	gr.synset=concept.entryTerms.join();
	gr.insert();
}
