sys_script_fix.script:
var keywordsAdded=0;
var keywordsSkipped=0;

try { 
	var r = new sn_ws.RESTMessageV2('x_463104_socrates.Enrichment', 'GetAllKeywords');
	var response = r.execute();
	var responseBody = response.getBody();
	var httpStatus = response.getStatusCode();
	var concepts = JSON.parse(responseBody);
	kewordsAdded=0;
	keyWordsSKipped=0;
	concepts.forEach(process);
	gs.info(concepts.length + ' concepts processed');
	gs.info(keywordsAdded + ' inserted, ' + keywordsSkipped + ' skipped');
}
catch(ex) {
	var message = ex.message;
}

function process(concept){
	var preferredName_=concept.preferredName.split(" ").join("_");
	var gr = new GlideRecord('kb_keyword');
	if (!gr.get('keyword', preferredName_)){
		gr.initialize(); 
		gr.keyword = preferredName_; 
		gr.insert();
		keywordsAdded++;
	}
	else{
		keywordsSkipped++;
	}
}
